document.addEventListener('DOMContentLoaded', () => {
    // Sample gallery data - In production, this would come from an API
    const galleryItems = [
        {
            id: 1,
            src: 'assets/gallery/event1.jpg',
            thumbnail: 'data:image/jpeg;base64,',
            caption: 'مهرجان الربيع السنوي 2024',
            category: 'events'
        },
        {
            id: 2,
            src: 'assets/gallery/community1.jpg',
            thumbnail: 'data:image/jpeg;base64,',
            caption: 'لقاء مجتمعي في المركز الثقافي',
            category: 'community'
        },
        {
            id: 3,
            src: 'assets/gallery/nature1.jpg',
            thumbnail: 'data:image/jpeg;base64,',
            caption: 'حديقة جديدة عرطوز في فصل الربيع',
            category: 'nature'
        }
    ];

    const galleryGrid = document.querySelector('.gallery-grid');
    const filterButtons = document.querySelectorAll('.filter-buttons button');
    const modal = document.getElementById('imageModal');
    const modalImg = document.getElementById('modalImage');
    const modalCaption = document.getElementById('modalCaption');
    const closeModal = document.querySelector('.close-modal');
    const uploadInput = document.getElementById('imageUpload');

    // Initialize gallery
    displayGallery('all');

    // Add filter functionality
    filterButtons.forEach(button => {
        button.addEventListener('click', () => {
            const filter = button.getAttribute('data-filter');
            
            // Update active button
            filterButtons.forEach(btn => btn.classList.remove('active'));
            button.classList.add('active');
            
            // Display filtered images
            displayGallery(filter);
        });
    });

    // Modal functionality
    closeModal.addEventListener('click', () => {
        modal.style.display = 'none';
    });

    window.addEventListener('click', (e) => {
        if (e.target === modal) {
            modal.style.display = 'none';
        }
    });

    // Image upload functionality
    uploadInput.addEventListener('change', handleImageUpload);

    function displayGallery(filter) {
        // Clear current gallery
        galleryGrid.innerHTML = '';
        
        // Filter and display images
        const filteredImages = filter === 'all' 
            ? galleryItems 
            : galleryItems.filter(item => item.category === filter);
        
        filteredImages.forEach(image => {
            const galleryItem = createGalleryItem(image);
            galleryGrid.appendChild(galleryItem);
        });
    }

    function createGalleryItem(image) {
        const item = document.createElement('div');
        item.className = 'gallery-item';
        
        item.innerHTML = `
            <img src="${image.thumbnail}" alt="${image.caption}" loading="lazy">
            <div class="gallery-item-overlay">
                <h3>${image.caption}</h3>
                <button class="btn btn-primary btn-sm">عرض</button>
            </div>
        `;
        
        // Add click event to show modal
        item.addEventListener('click', () => {
            modalImg.src = image.src;
            modalCaption.textContent = image.caption;
            modal.style.display = 'block';
        });
        
        return item;
    }

    function handleImageUpload(e) {
        const file = e.target.files[0];
        if (!file) return;

        // Validate file type
        if (!file.type.startsWith('image/')) {
            alert('الرجاء اختيار ملف صورة صالح');
            return;
        }

        // Validate file size (max 5MB)
        if (file.size > 5 * 1024 * 1024) {
            alert('حجم الصورة يجب أن لا يتجاوز 5 ميغابايت');
            return;
        }

        // Here you would typically upload the file to your server
        // For now, we'll just show a success message
        alert('تم رفع الصورة بنجاح!');
        
        // Reset the input
        e.target.value = '';
    }
});
