document.addEventListener('DOMContentLoaded', () => {
    // Sample news data - In production, this would come from an API
    const newsItems = [
        {
            id: 1,
            title: 'افتتاح المكتبة العامة الجديدة',
            date: '2024-03-10',
            category: 'local',
            image: 'data:image/jpeg;base64,',
            description: 'تم افتتاح المكتبة العامة الجديدة في حي جديدة عرطوز. المكتبة مجهزة بأحدث الكتب والمراجع وتوفر خدمات متنوعة للقراء.'
        },
        {
            id: 2,
            title: 'حملة تشجير في الحي',
            date: '2024-03-15',
            category: 'activities',
            image: 'data:image/jpeg;base64,',
            description: 'انطلقت حملة تشجير واسعة في الحي بمشاركة المجتمع المحلي. تم زراعة أكثر من 100 شجرة في مختلف أنحاء الحي.'
        },
        {
            id: 3,
            title: 'نجاح مبادرة التعليم المجتمعي',
            date: '2024-03-20',
            category: 'community',
            image: 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcR6QekXu5K3BoyAT4mljiphwu77hGJbQBbu7w&s',
            description: 'حققت مبادرة التعليم المجتمعي نجاحاً كبيراً في دعم الطلاب المحتاجين. شارك أكثر من 50 متطوع في تقديم دروس مجانية.'
        }
    ];

    const newsGrid = document.querySelector('.news-grid');
    const filterButtons = document.querySelectorAll('.filter-buttons button');

    // Initialize news grid
    displayNews('all');

    // Add filter functionality
    filterButtons.forEach(button => {
        button.addEventListener('click', () => {
            const filter = button.getAttribute('data-filter');
            
            // Update active button
            filterButtons.forEach(btn => btn.classList.remove('active'));
            button.classList.add('active');
            
            // Display filtered news
            displayNews(filter);
        });
    });

    function displayNews(filter) {
        // Clear current news
        newsGrid.innerHTML = '';
        
        // Filter and display news
        const filteredNews = filter === 'all' 
            ? newsItems 
            : newsItems.filter(item => item.category === filter);
        
        filteredNews.forEach(news => {
            const newsCard = createNewsCard(news);
            newsGrid.appendChild(newsCard);
        });
    }

    function createNewsCard(news) {
        const article = document.createElement('article');
        article.className = 'news-card';
        
        article.innerHTML = `
            <div class="news-image">
                <img src="${news.image}" alt="${news.title}" onerror="this.src='assets/news/placeholder.jpg'">
            </div>
            <div class="news-content">
                <span class="news-date">${formatDate(news.date)}</span>
                <h3>${news.title}</h3>
                <p>${news.description}</p>
                <a href="#" class="btn btn-primary">اقرأ المزيد</a>
            </div>
        `;
        
        return article;
    }

    function formatDate(dateStr) {
        const options = { 
            year: 'numeric', 
            month: 'long', 
            day: 'numeric'
        };
        return new Date(dateStr).toLocaleDateString('ar-SY', options);
    }
});
